import React from 'react';
import { animated, useSprings } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
const staticStars = Array.from({ length: 50 }, (_, index) => ({
id: `static-${index}`,
x: Math.random() * window.innerWidth,
y: Math.random() * window.innerHeight,
opacity: Math.random(),
}));

const [fallingStars, setFallingStars] = useSprings(staticStars.length, staticStars.map(() => ({
x: 0,
y: window.innerHeight,
opacity: 0,
})));

return (
<animated.div id="animated-background-night">
{staticStars.map((star, index) => (
<animated.div
key={star.id}
className="star"
style={{
left: star.x,
top: star.y,
opacity: star.opacity,
}}
/>
))}
{fallingStars.map((style, index) => (
<animated.div
key={staticStars[index].id}
className="falling-star"
style={{
left: style.x,
top: style.y,
opacity: style.opacity,
}}
/>
))}
</animated.div>
);
};

export default AnimatedBackgroundNight;
