import React from 'react';
import { animated, useSpring } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
const stars = Array.from({ length: 50 }, (_, index) => ({
id: index,
x: Math.random() * window.innerWidth,
y: Math.random() * window.innerHeight,
opacity: Math.random(),
}));

const fallingStars = Array.from({ length: 20 }, (_, index) => ({
id: index,
x: Math.random() * window.innerWidth,
y: 0,
opacity: Math.random(),
speed: 0.5 + Math.random() * 2, // Adjust the falling speed as needed
}));

const starProps = useSpring({
from: { translateY: 0 },
to: { translateY: window.innerHeight },
config: { duration: 5000 }, // Adjust the duration of the falling animation
reset: true,
});

return (
<animated.div id="animated-background-night">
{stars.map((star) => (
<animated.div
key={star.id}
className="star"
style={{
left: star.x,
top: star.y,
opacity: star.opacity,
}}
/>
))}
{fallingStars.map((fallingStar) => (
<animated.div
key={fallingStar.id}
className="falling-star"
style={{
left: fallingStar.x,
top: fallingStar.y,
opacity: fallingStar.opacity,
transform: starProps.translateY.interpolate(
(value) => `translateY(${value}px)`
),
}}
/>
))}
</animated.div>
);
};

export default AnimatedBackgroundNight;
