// FallingStar.js
import React, { useEffect } from 'react';
import { useSpring, animated } from 'react-spring';
import Star from './star';

const FallingStar = ({ x, falling }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [x, -10], // Starting position off-screen
  }));

  useEffect(() => {
    const interval = setInterval(() => {
      set({ opacity: 1, xy: [x, window.innerHeight] }); // Ending position off-screen
    }, Math.random() * 5000);

    return () => clearInterval(interval);
  }, [set, x]);

  useEffect(() => {
    const timeout = setTimeout(() => {
      set({ opacity: 0, xy: [x, -10] }); // Starting position off-screen
    }, Math.random() * 10000); // Delay before next falling, e.g., 10 seconds

    return () => clearTimeout(timeout);
  }, [set, x]);

  return <Star x={props.xy[0]} y={props.xy[1]} opacity={props.opacity} />;
};

export default FallingStar;
