import React from 'react';
import { animated, useSpring } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
const stars = Array.from({ length: 50 }, (_, index) => ({
id: index,
x: Math.random() * window.innerWidth,
y: Math.random() * window.innerHeight,
opacity: Math.random(),
}));

const fallingStars = useSpring({
    from: { translateY: -100, opacity: 1 }, // изменено начальное значение opacity
    to: async (next) => {
    while (true) {
    await next({ translateY: window.innerHeight + 100, opacity: 0 });
    await next({ translateY: -100, opacity: 1 }); // изменено конечное значение opacity
    }
    },
    config: { duration: 5000 },
    });

return (
<animated.div id="animated-background-night">
{stars.map((star) => (
<animated.div
key={star.id}
className="star"
style={{
left: star.x,
top: star.y,
opacity: star.opacity,
}}
/>
))}
<animated.div
className="falling-star"
style={{
transform: fallingStars.translateY.to((y) => `translateY(${y}px)`),
opacity: fallingStars.opacity,
}}
/>
</animated.div>
);
};

export default AnimatedBackgroundNight;
