import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    y: -10,
    onRest: () => set({ opacity: 0, y: -10 }), // Reset position after animation
  }));

  React.useEffect(() => {
    set({ opacity: 1, y: window.innerHeight });
  }, [set]);

  return <animated.div className="falling-star" style={{ left: x, top: props.y, opacity: props.opacity }} />;
};

const AnimatedBackgroundNight = () => {
  const stars = Array.from({ length: 50 }, (_, index) => ({
    id: index,
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
    opacity: Math.random(),
  }));

  const fallingStars = Array.from({ length: 10 }, (_, index) => ({
    id: index,
    x: Math.random() * window.innerWidth,
  }));

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      {fallingStars.map((fallingStar) => (
        <FallingStar key={fallingStar.id} x={fallingStar.x} />
      ))}
    </div>
  );
};

export default AnimatedBackgroundNight;
