import React from 'react';
import { animated, useSpring } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
const staticStars = Array.from({ length: 50 }, (_, index) => ({
id: `static-${index}`,
x: Math.random() * window.innerWidth,
y: Math.random() * window.innerHeight,
opacity: Math.random(),
}));

const [fallingStars, setFallingStars] = useSpring(() => ({
from: { x: 0, y: 0, opacity: 0 },
to: { x: 0, y: window.innerHeight, opacity: 1 },
onRest: () => setFallingStars({ to: { x: 0, y: window.innerHeight, opacity: 0 } }),
}));

return (
<animated.div id="animated-background-night">
{staticStars.map((star) => (
<animated.div
key={star.id}
className="star"
style={{
left: star.x,
top: star.y,
opacity: star.opacity,
}}
/>
))}
{fallingStars.to((style, item) => (
<animated.div
key={item.id}
className="falling-star"
style={{
left: style.x,
top: style.y,
opacity: style.opacity,
}}
/>
))}
</animated.div>
);
};

export default AnimatedBackgroundNight;
