import React from 'react';
import { useSpring, animated } from 'react-spring';

const AnimatedBackgroundNight = () => {
const starStyles = {
width: '2px',
height: '2px',
backgroundColor: 'white',
borderRadius: '50%',
position: 'absolute',
};

const stars = Array.from({ length: 50 }, (_, index) => ({
id: index,
x: Math.random() * window.innerWidth,
y: Math.random() * window.innerHeight,
opacity: Math.random(),
}));

const [{ opacity }, set] = useSpring(() => ({ opacity: 1 }));

return (
<animated.div
style={{
position: 'fixed',
top: 0,
left: 0,
width: '100%',
height: '100%',
background: 'linear-gradient(to bottom, #001e3c, #01446e)',
overflow: 'hidden',
opacity,
}}
onMouseMove={({ clientX: x, clientY: y }) => set({ xys: [x, y, 1] })}
>
{stars.map((star) => (
<animated.div
key={star.id}
style={{
...starStyles,
left: star.x,
top: star.y,
opacity: star.opacity,
}}
/>
))}
</animated.div>
);
};

export default AnimatedBackgroundNight;
