import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(
    Array.from({ length: 10 }, (_, index) => ({
      id: index,
      x: Math.random() * window.innerWidth,
      y: -10, // starting off-screen
      opacity: 0, // initially invisible
    }))
  );

  const stars = Array.from({ length: 50 }, (_, index) => ({
    id: index,
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
    opacity: Math.random(),
  }));

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      {fallingStars.map((fallingStar) => {
        const props = useSpring({
          to: async (next) => {
            await next({ opacity: 1, y: window.innerHeight, config: { duration: 2000 } });
            await next({ opacity: 0, y: -10 });
          },
          from: { opacity: 0, y: -10 },
        });

        return (
          <animated.div
            key={fallingStar.id}
            className="falling-star"
            style={{
              left: fallingStar.x,
              top: props.y,
              opacity: props.opacity,
            }}
          />
        );
      })}
    </div>
  );
};

export default AnimatedBackgroundNight;
