import React, { useEffect, useState } from 'react';
import FallingStar from './FallingStar';
import Stars from './Stars';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(
    Array.from({ length: 2 }, (_, index) => ({
      id: index,
      x: Math.random() * window.innerWidth,
      falling: false,
    }))
  );

  useEffect(() => {
    fallingStars.forEach((fallingStar) => {
      const timeout = setTimeout(() => {
        setFallingStars((prevStars) =>
          prevStars.map((star) => (star.id === fallingStar.id ? { ...star, falling: true } : star))
        );
      }, Math.random() * 10000);
      return () => clearTimeout(timeout);
    });
  }, [fallingStars]);

  return (
    <div id="animated-background-night">
      <Stars />
      {fallingStars.map((fallingStar) => (
        fallingStar.falling && <FallingStar key={fallingStar.id} x={fallingStar.x} />
      ))}
    </div>
  );
};

export default AnimatedBackgroundNight;
