import React, { useEffect, useState, useMemo } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = () => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [Math.random() * window.innerWidth, -10],
  }));

  useEffect(() => {
    const minInterval = 2000;
    const maxInterval = 7000;

    const fall = () => {
      set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight] });
      setTimeout(() => {
        set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
        fall(); // Рекурсивный вызов для обеспечения постоянного падения с интервалом
      }, Math.random() * 5000);
    };

    const initialTimeout = setTimeout(() => {
      fall();
    }, minInterval + Math.random() * (maxInterval - minInterval));

    return () => clearTimeout(initialTimeout);
  }, [set]);

  return <animated.div className="falling-star" style={{ transform: props.xy.to((x, y) => `translate(${x}px, ${y}px)`), opacity: props.opacity }} />;
};

const AnimatedBackgroundNight = () => {
  const stars = useMemo(
    () =>
      Array.from({ length: 50 }, (_, index) => ({
        id: index,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        opacity: Math.random(),
      })),
    []
  );

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      <FallingStar />
    </div>
  );
};

export default AnimatedBackgroundNight;
