import React, { useEffect } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [-10, -10], // Starting position off-screen
    onRest: () => set({ opacity: 0, xy: [-10, -10] }),
  }));

  useEffect(() => {
    const interval = setInterval(() => {
      set({ opacity: 1, xy: [window.innerWidth + 10, window.innerHeight + 10] }); // Ending position off-screen
    }, Math.random() * 10000);

    return () => clearInterval(interval);
  }, [set]);

  return <animated.div className="falling-star" style={{ transform: props.xy.interpolate((x, y) => `translate(${x}px, ${y}px)`) }} />;
};

