import React from "react";
import { useSpring, animated } from 'react-spring';

const AnimatedBackgroundNight = () => {
const starStyles = {
width: '2px',
height: '2px',
backgroundColor: 'white',
borderRadius: '50%',
position: 'absolute',
};

const Star = () => {
    const { xy, opacity } = useSpring({
    from: { xy: [0, -100], opacity: 0 },
    to: { xy: [0, 1000], opacity: 1 },
    reset: true,
    reverse: true,
    onRest: () => set({ xy: [0, -100] }),
    });

    return (
        <animated.div
        style={{
        position: 'absolute',
        width: '2px',
        height: '2px',
        backgroundColor: 'white',
        borderRadius: '50%',
        transform: xy.interpolate((x, y) => `translate(${x}px, ${y}px)`),
        opacity,
        }}
        />
        );
        };
        
        const AnimatedBackgroundNight = () => {
        return (
        <div style={{ position: 'relative', width: '100%', height: '100vh' }}>
        <Star />
        <Star />
        {/* Добавьте дополнительные звезды по вашему выбору */}
        </div>
        );
    };
}
export default AnimatedBackgroundNight;
