import React, { useEffect, useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [x, -10],
  }));

  useEffect(() => {
    const minInterval = 5000;
    const maxInterval = 7000;
    const interval = setInterval(() => {
      set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight] });
    }, minInterval + Math.random() * (maxInterval - minInterval));

    const timeout = setTimeout(() => {
      set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
    }, Math.random() * 5000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, [set]);

  return <animated.div className="falling-star" style={{ transform: props.xy.to((x, y) => `translate(${x}px, ${y}px)`) }} />;
};

const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(Array.from({ length: 10 }, (_, index) => ({ id: index, falling: false })));

  const startFallingStar = (fallingStar) => {
    const timeout = setTimeout(() => {
      setFallingStars((prevStars) =>
        prevStars.map((star) => (star.id === fallingStar.id ? { ...star, falling: true } : star))
      );
    }, Math.random() * 10000);

    return () => clearTimeout(timeout);
  };

  useEffect(() => {
    fallingStars.forEach(startFallingStar);
  }, [fallingStars]);

  return (
    <div id="animated-background-night">
      {fallingStars.map((fallingStar) => fallingStar.falling && <FallingStar key={fallingStar.id} x={Math.random() * window.innerWidth} />)}
    </div>
  );
};

export default AnimatedBackgroundNight;
