import React, { useEffect, useState, useMemo } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = () => {
    const [props, set] = useSpring(() => ({
      opacity: 0,
      xy: [Math.random() * window.innerWidth, -10], // Starting position off-screen
      onRest: () => set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] }),
    }));
  
    useEffect(() => {
      const interval = setInterval(() => {
        set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight] }); // Ending position off-screen
      }, Math.random() * 10000);
  
      return () => clearInterval(interval);
    }, [set]);
  
    useEffect(() => {
      // Добавляем дополнительный временной интервал перед началом следующего падения
      const timeout = setTimeout(() => {
        set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
      }, Math.random() * 5000); // Задержка перед следующим падением, например, 5 секунд
  
      return () => clearTimeout(timeout);
    }, [set]);
  
    return <animated.div className="falling-star" style={{ transform: props.xy.interpolate((x, y) => `translate(${x}px, ${y}px)`) }} />;
  };


const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(
    Array.from({ length: 2 }, (_, index) => ({
      id: index,
      x: Math.random() * window.innerWidth,
      falling: false,
    }))
  );

  useEffect(() => {
    fallingStars.forEach((fallingStar) => {
      const timeout = setTimeout(() => {
        setFallingStars((prevStars) =>
          prevStars.map((star) => (star.id === fallingStar.id ? { ...star, falling: true } : star))
        );
      }, Math.random() * 10000);
      return () => clearTimeout(timeout);
    });
  }, [fallingStars]);

  const stars = useMemo(
    () =>
      Array.from({ length: 50 }, (_, index) => ({
        id: index,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        opacity: Math.random(),
      })),
    []
  );

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      {fallingStars.map((fallingStar) => (
        fallingStar.falling && <FallingStar key={fallingStar.id} x={fallingStar.x} />
      ))}
    </div>
  );
};

export default AnimatedBackgroundNight;
