import React, { useEffect } from 'react';
import { useSpring } from 'react-spring';
import Star from './star';

const FallingStar = () => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [Math.random() * window.innerWidth, -10],
  }));

  useEffect(() => {
    const interval = setInterval(() => {
      set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight] });
    }, Math.random() * 5000);

    return () => clearInterval(interval);
  }, [set]);

  useEffect(() => {
    const timeout = setTimeout(() => {
      set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
    }, Math.random() * 10000);

    return () => clearTimeout(timeout);
  }, [set]);

  return <Star x={props.xy[0]} y={props.xy[1]} opacity={props.opacity} />;
};

export default FallingStar;
