import React from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const AnimatedBackgroundNight = () => {
    const stars = Array.from({ length: 50 }, (_, index) => ({
    id: index,
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
    opacity: Math.random(),
    }));
    
    const [{ opacity }, set] = useSpring(() => ({ opacity: 1 }));
    
    return (
    <animated.div
    className="animated-background" // Используем классы из стилей
    onMouseMove={({ clientX: x, clientY: y }) => set({ xys: [x, y, 1] })}
    >
    {stars.map((star) => (
    <animated.div
    key={star.id}
    className="star" // Используем классы из стилей
    style={{
    left: star.x,
    top: star.y,
    opacity: star.opacity,
    }}
    />
    ))}
    </animated.div>
    );
};

export default AnimatedBackgroundNight;
