import React, { useEffect, useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    y: 0, // Начальная координата вверху экрана
    onRest: () => set({ opacity: 0, y: 0 }),
  }));

  useEffect(() => {
    const interval = setInterval(() => {
      set({ opacity: 1, y: window.innerHeight }); // Конечная координата внизу экрана
    }, Math.random() * 10000);
    return () => clearInterval(interval);
  }, [set]);

  return <animated.div className="falling-star" style={{ left: x, top: props.y, opacity: props.opacity }} />;
};

const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(
    Array.from({ length: 5 }, (_, index) => ({
      id: index,
      x: Math.random() * window.innerWidth,
      falling: false,
    }))
  );

  useEffect(() => {
    fallingStars.forEach((fallingStar) => {
      const timeout = setTimeout(() => {
        setFallingStars((prevStars) =>
          prevStars.map((star) => (star.id === fallingStar.id ? { ...star, falling: true } : star))
        );
      }, Math.random() * 10000);
      return () => clearTimeout(timeout);
    });
  }, [fallingStars]);

  const stars = Array.from({ length: 50 }, (_, index) => ({
    id: index,
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
    opacity: Math.random(),
  }));

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      {fallingStars.map((fallingStar) => (
        fallingStar.falling && <FallingStar key={fallingStar.id} x={fallingStar.x} />
      ))}
    </div>
  );
};

export default AnimatedBackgroundNight;
