import React, { Component } from 'react';
import { animated } from 'react-spring';
import './night-bg.css';

class AnimatedBackgroundNight extends Component {
  constructor(props) {
    super(props);

    this.state = {
      stars: Array.from({ length: 50 }, (_, index) => ({
        id: index,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        opacity: Math.random(),
      })),
      
    };
  }

  render() {
    const { stars } = this.state;

    return (
      <animated.div id="animated-background-night">
        {stars.map((star) => (
          <animated.div
            key={star.id}
            className="star"
            style={{
              left: star.x,
              top: star.y,
              opacity: star.opacity,
            }}
          />
        ))}
      </animated.div>
    );
  }
}

export default AnimatedBackgroundNight;
