import React, { useEffect } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [Math.random(0, window.innerWidth), -10], // Starting position off-screen
  }));

  useEffect(() => {
    const interval = setInterval(() => {
      set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight+10] }); // Ending position off-screen
    }, Math.random() * 2500);

    return () => clearInterval(interval);
  }, [set, x]);

  useEffect(() => {
    const timeout = setTimeout(() => {
      set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
    }, Math.random() * 10000); // Задержка перед следующим падением, например, 5 секунд

    return () => clearTimeout(timeout);
  }, [set, x]);

  return <animated.div className="falling-star" 
  style={{ transform: props.xy.to((x, y) => `translate(${x}px, ${y}px)`) }} />;
};

export default FallingStar;
