import React, { useEffect, useState, useMemo } from 'react';
import { useSpring, animated } from 'react-spring';
import './night-bg.css';

const FallingStar = ({ x, onReset }) => {
  const [props, set] = useSpring(() => ({
    opacity: 0,
    xy: [x, -10],
  }));

  useEffect(() => {
    const minInterval = 5000;
    const maxInterval = 7000;

    const interval = setInterval(() => {
      set({ opacity: 1, xy: [Math.random() * window.innerWidth, window.innerHeight] });
    }, minInterval + Math.random() * (maxInterval - minInterval));

    const timeout = setTimeout(() => {
      set({ opacity: 0, xy: [Math.random() * window.innerWidth, -10] });
      onReset();
    }, Math.random() * 5000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, [set, onReset]);

  return <animated.div className="falling-star" style={{ transform: props.xy.to((x, y) => `translate(${x}px, ${y}px)`) }} />;
};

const AnimatedBackgroundNight = () => {
  const [fallingStars, setFallingStars] = useState(Array.from({ length: 10 }, (_, index) => ({ id: index, falling: false })));

  const resetStar = (id) => {
    setFallingStars((prevStars) => prevStars.map((star) => (star.id === id ? { ...star, falling: false } : star)));
  };

  const startFallingStar = (fallingStar) => {
    const timeout = setTimeout(() => {
      resetStar(fallingStar.id);
    }, Math.random() * 10000);

    return () => clearTimeout(timeout);
  };

  useEffect(() => {
    fallingStars.forEach(startFallingStar);
  }, [fallingStars]);

  const stars = useMemo(
    () =>
      Array.from({ length: 50 }, (_, index) => ({
        id: index,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        opacity: Math.random(),
      })),
    []
  );

  return (
    <div id="animated-background-night">
      {stars.map((star) => (
        <animated.div
          key={star.id}
          className="star"
          style={{
            left: star.x,
            top: star.y,
            opacity: star.opacity,
          }}
        />
      ))}
      {fallingStars.map((fallingStar) => fallingStar.falling && <FallingStar key={fallingStar.id} x={Math.random() * window.innerWidth} onReset={() => resetStar(fallingStar.id)} />)}
    </div>
  );
};

export default AnimatedBackgroundNight;
